DB_D("DROP TABLE IF EXISTS `lzh_article`;");
DB_C("CREATE TABLE `lzh_article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL,
  `art_info` varchar(255) NOT NULL,
  `art_keyword` varchar(200) NOT NULL,
  `art_content` text NOT NULL,
  `art_writer` varchar(20) NOT NULL,
  `art_time` int(10) unsigned NOT NULL DEFAULT '0',
  `type_id` smallint(5) unsigned NOT NULL,
  `art_url` varchar(200) NOT NULL,
  `art_img` varchar(200) NOT NULL,
  `art_userid` smallint(5) unsigned NOT NULL,
  `sort_order` int(10) unsigned NOT NULL,
  `art_click` int(10) unsigned NOT NULL DEFAULT '0',
  `art_set` int(1) unsigned NOT NULL DEFAULT '0',
  `art_attr` tinyint(4) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `type_id` (`type_id`)
) ENGINE=MyISAM AUTO_INCREMENT=109 DEFAULT CHARSET=utf8");
DB_I("replace  into `lzh_article` values('55','关于我们','','','<div style=\"layout-grid:  15.6pt none\" class=\"Section0\"><p style=\"margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 宋体; font-size: 10.5pt\">成立背景</span><span style=\"font-family: 宋体; font-size: 10.5pt\">:</span></p></div>','杨烁','1345827824','326','','','0','0','0','0','0');");
DB_I("replace  into `lzh_article` values('56','法律政策','','','<p>&nbsp;</p><p><strong>1.&nbsp;</strong><strong>关于民间借贷行为</strong><br />民间借贷是指自然人之间、自然人与法人之间、自然人与其它组织之间借贷。只要双方当事人具有完全民事行为能力且意见表示真实即可认定有效，因借贷产生的担保或抵押也相应有效，但利率不得超过人民银行规定的相关利率。民间借贷是一种直接融资渠道，是民间金融的一种表现形式，民间借贷业的发展是对现今金融体系的补充。</p><p>&nbsp;</p><p>平台上借入借出的利率严格按照国家相关法律规定执行。</p><p>《中华人民共和国合同法》第二百一十一条</p><p>自然人之间的借款合同对支付利息没有约定或者约定不明确的，视为不支付利息。自然人之间的借款合同约定支付利息的，借款的利率不得违反国家有关限制借款利率的规定。</p><p>《最高人民法院关于人民法院审理借贷案件的若干意见》<br />民间借贷的利率可以适当高于银行的利率，各地人民法院可根据本地区的实际情况具体掌握，但最高不得超过银行同类贷款利率的四倍（包含利率本数）。超出此限度的，超出部分的利息不予保护。</p><p>中国人民银行现行金融机构人民币贷款基准利率（单位：年利率%，2011年2月9日起实施）<br /><em>六个月以内（含六个月）5.60<br />六个月至一年（含一年）6.06<br />一至三年（含三年） 6.10<br />三至五年（含五年） 6.45<br />五年以上 6.60</em></p><p>&nbsp;</p><p><strong>3.&nbsp;</strong><strong>关于担保</strong><br />平台上的所有担保行为均为连带责任保证。</p><p>《中华人民共和国合同法》第一百九十八条</p><p>订立借款合同，贷款人可以要求借款人提供担保。担保依照《中华人民共和国担保法》的规定。</p><p>《最高人民法院关于人民法院审理借贷案件的若干意见》<br />有保证人的借贷债务到期后，债务人有清偿能力的，由债务人承担责任；债务人无能力清偿、无法清偿或者债务人下落不明的，由保证人承担连带责任。</p><p>《中华人民共和国担保法》第四条</p><p>第三人为债务人向债权人提供担保时，可以要求债务人提供反担保。反担保适用本法担保的规定。<br />第二十一条、保证担保的范围包括主债权及利息、违约金、损害赔偿金和实现债权的费用。保证合同另有约定的，按照约定。</p><p>当事人对保证担保的范围没有约定或者约定不明确的，保证人应当对全部债务承担责任。</p><p>第二十二条、保证期间，债权人依法将主债权转让给第三人的，保证人在原保证担保的范围内继续承担保证责任。保证合同另有约定的，按照约定。</p><table class=\"\" width=\"100%\"><tbody><tr><td>&nbsp;</td><td>&nbsp;</td></tr></tbody></table>','杨烁','1345828539','313','','','0','0','0','0','0');");
DB_I("replace  into `lzh_article` values('57','法律顾问','','','<p><font style=\"font-size: 20px\" color=\"#000000\"><b>鼓励和引导民间资本进入金融服务领域</b> </font></p><p>&nbsp;</p><hr /><p>&nbsp;</p><div class=\"spctrl\">&nbsp;</div><p><font style=\"font-size: 20px\" color=\"#000000\">　　允许民间资本兴办金融机构。在加强有效监管、促进规范经营、防范金融风险的前提下，放宽对金融机构的股比限制。支持民间资本以入股方式参与商业银行的增资扩股，参与农村信用社、城市信用社的改制工作。鼓励民间资本发起或参与设立村镇银行、贷款公司、</font><a href=\"http://baike.baidu.com/view/2123375.htm\" target=\"_blank\"><font style=\"font-size: 20px\" color=\"#000000\">农村资金互助社</font></a><font style=\"font-size: 20px\" color=\"#000000\">等金融机构，放宽村镇银行或社区银行中法人银行最低出资比例的限制。落实</font><a href=\"http://baike.baidu.com/view/1887800.htm\" target=\"_blank\"><font style=\"font-size: 20px\" color=\"#000000\">中小企业贷款</font></a><font style=\"font-size: 20px\" color=\"#000000\">税前全额拨备损失准备金政策，简化</font><a href=\"http://baike.baidu.com/view/938327.htm\" target=\"_blank\"><font style=\"font-size: 20px\" color=\"#000000\">中小金融机构</font></a><font style=\"font-size: 20px\" color=\"#000000\">呆账核销审核程序。适当放宽</font><a href=\"http://baike.baidu.com/view/1594605.htm\" target=\"_blank\"><font style=\"font-size: 20px\" color=\"#000000\">小额贷款公司</font></a><font style=\"font-size: 20px\" color=\"#000000\">单一投资者持股比例限制，对小额贷款公司的涉农业务实行与村镇银行同等的</font><a href=\"http://baike.baidu.com/view/694886.htm\" target=\"_blank\"><font style=\"font-size: 20px\" color=\"#000000\">财政补贴政策</font></a><font style=\"font-size: 20px\" color=\"#000000\">。支持民间资本发起设立信用担保公司，完善信用担保公司的</font><a href=\"http://baike.baidu.com/view/3662953.htm\" target=\"_blank\"><font style=\"font-size: 20px\" color=\"#000000\">风险补偿机制</font></a><font style=\"font-size: 20px\" color=\"#000000\">和风险分担机制。鼓励民间资本发起设立金融中介服务机构，参与证券、保险等金融机构的改组改制。</font></p>','杨烁','1345828660','312','','','0','0','0','0','0');");
DB_I("replace  into `lzh_article` values('59','催欠公告','','','<p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt; font-weight: bold\">逾期催收方式与措施</span></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">用户借款后如确实遇到困难，必须主动联系网站说明原因，并做出还款计划，网站将为其进行债务重组，尽量帮助其缓解困难。</span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">用户如借款逾期以后，不接电话或恶意停机躲避债务等，网站将派人上门催收，除按逾期天数收取违约金和罚息，另外还将收取上门催收费用。</span><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">对于恶意骗款后逃跑的用户，网站将采取一切必要措施打击，收回欠款，并采取法律措施追究其责任。</span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\">&nbsp;</p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt; font-weight: bold\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 警告一切存心不良人士，请及时处理欠款，每一天的逾期，最终的后果由你本人承担。</span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt; font-weight: bold\">特别条款</span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">1</font><font face=\"宋体\">、借款人保证：借款人的借款用于合法用途，不得将所借款项用于任何违法活动</font><font face=\"微软雅黑\">(</font><font face=\"宋体\">包括但不限于赌博、吸毒、贩毒、卖淫嫖娼等</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">及一切高风险投资</font><font face=\"微软雅黑\">(</font><font face=\"宋体\">如证券期货、彩票等</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">。如借款人违反前述保证或有违反前述保证的嫌疑，则出借人有权采取下列措施：</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">(1)</font><font face=\"宋体\">宣布提前收回全部借款</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">(2)</font><font face=\"宋体\">出借人向公安等行政机关举报，追回此款并追究借款人的刑事责任，出借人和借款人均同意并授权本网站采取前述措施。</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">2</font><font face=\"宋体\">、本网站仅作为网友之间小额资金互助平台，借款人和出借人均不得利用本网站平台进行信用卡套现和其他</font><font face=\"微软雅黑\">(</font><font face=\"宋体\">如洗钱</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">不正当交易行为，否则出借人、借款人和本网站有权向公安等行政机关举报，追究其相关法律责任。</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">(1)</font><font face=\"宋体\">若借款者逾期仍未还款，除正常利息以外，对应还款额未还部分，还应支付千分之八</font><font face=\"微软雅黑\">/</font><font face=\"宋体\">天的逾期利息作为催收费用；逾期应还款由本网站通过短信、电话、上门等方式进行催收，暂不公开个人隐私资料。</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">(2)</font><font face=\"宋体\">借款者逾期还款，造成投资者因此支付的费用</font><font face=\"微软雅黑\">(</font><font face=\"宋体\">包括但不限于律师费</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">由借款者承担。本网站有权将借款人的有关资料正式备案在</font><font face=\"Times New Roman\">“</font><font face=\"宋体\">不良信用记录</font><font face=\"Times New Roman\">”</font><font face=\"宋体\">，列入全国个人信用评级体系的黑名单</font><font face=\"微软雅黑\">(</font><font face=\"Times New Roman\">“</font><font face=\"宋体\">不良信用记录</font><font face=\"Times New Roman\">”</font><font face=\"宋体\">数据将为银行、电信、担保公司、人才中心等有关机构提供个人不良信用信息</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">，同时保留对借款者采取法律措施的权利，由此所产生的所有法律后果将由借款者来承担。</font></span></p><p style=\"margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本页面内容仅供参考，以网站的公告与具体规定为准。</span></span></p><!--EndFragment-->','刘永达','1346397238','321','','','0','0','0','0','0');");
DB_I("replace  into `lzh_article` values('63','媒体宣传','','','','刘永达','1346553469','8','','','0','0','0','0','0');");
DB_I("replace  into `lzh_article` values('78','网站收费标准','','','<div style=\"layout-grid:  15.6pt none\" class=\"Section0\"><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">1</font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">、投资累计充值</span></b><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">5</font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">万元以上（含</span></b><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">5</font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">万）返还一年</span></b><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">VIP </font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">费用</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"color: #ff9900; font-size: 12pt\"><span><font face=\"Times New Roman\">&nbsp;&nbsp; </font></span></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">单笔充值</span></b><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">2</font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">万以上（含</span></b><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">2</font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">万）返还一年</span></b><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">VIP </font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">费用</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">2</font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">、免收投资人利息管理费</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">3</font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">、线上，线上下充值免收手续费</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"color: #ff9900; font-size: 12pt\"><span><font face=\"Times New Roman\">&nbsp; </font></span></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">为降低公司运营成本，鼓励线下充值。通过线下充值返</span></b><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">1‰</font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">的奖励</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"color: lime; font-size: 12pt\"><font face=\"Times New Roman\">&nbsp;</font></span></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">充值免费：</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">线下充值免费</span><span style=\"font-family: 宋体; color: #333333\">（</span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">为了防止信用卡套现，没有投资不能许提现或都充值后</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">7</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天内不许提现）。</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">线上充值免费</span><span style=\"color: #333333\"><font face=\"Times New Roman\"> </font></span><span style=\"font-family: 宋体; color: #333333\">（</span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">通过线下充值返</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1‰</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">的奖励，若未进行投标提现，奖励取消）。</span></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">提现费用：</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">投资人提现免费（提现每笔限额</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0-44980</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">元）</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">借款人提现每笔</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">元（提现每笔限额</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0-44980</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">元）</span></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">认证费用：</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">身份认证首次免费，若认证错误再次认证</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">5</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">元</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">/</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">次，由认证机构收取</span></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"color: #ff9900; font-size: 12pt\"><font face=\"Times New Roman\">VIP</font></span></b><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">会员费用：</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">120</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">元</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">/</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">年，含实名认证、手机验证和电话验证。（网站只有</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">VIP</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">会员才可发启借款标）</span></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">邀请好友返现：</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">邀请好友成为</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">VIP</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">会员后：返还</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">30</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">元到邀请人九洲贷账户</span></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">逾期罚息：</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">逾期费率</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">*</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">应还款本息</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">*</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">逾期天数（逾期费率：</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">4‰</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">）</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"color: #333333\"><font face=\"Times New Roman\">&nbsp;</font></span></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"font-family: 宋体; color: #ff9900; font-size: 12pt\">借款管理费：</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">在借款成功时需缴纳借款管理费，借款不成功不收取，具体如下：</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #339966; font-size: 12pt\">抵押标、信用标（等额本息按月还款）</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">个月管理费为本金</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">个月内管理费为本金</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，每增加一个月管理费用增加</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.2%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，例如借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">3</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">个月管理费用为借款本金的</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2.2%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，以此类推。管理费在用户借款成功后在借到款项内扣除；</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #339966; font-size: 12pt\">抵押标、信用标（月付息到期一次还本）</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">个月管理费为本金</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">个月内管理费为本金</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2.3%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，每增加一个月管理费用增加</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.3%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，例如借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">3</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">个月管理费用为借款本金的</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2.6%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，以此类推。管理费在用户借款成功后在借到款项内扣除；</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #339966; font-size: 12pt\">天标、信用天标、抵押天标：</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天管理费为本金</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.4%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天以上每增加一天管理费增加</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.04%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，例如借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天管理费为</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.44%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，以此类推。管理费在用户借款成功后在借到款项内扣除。</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #339966; font-size: 12pt\">秒标、净值秒标：</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">为本金</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.1%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，实际还款时间为满标审核后自动还本息。</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #339966; font-size: 12pt\">净值标（等额本息按月还款、月付息到期一次还本）</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">个月管理费为本金</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.5%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">个月内管理费为本金</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，每增加一个月管理费用增加</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.5%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，例如借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">3</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">个月管理费用为借款本金的</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1.5%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，以此类推。管理费在用户借款成功后在借到款项内扣除；</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #339966; font-size: 12pt\">净值天标：</span></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天管理费为本金</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.2%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天以上每增加一天管理费增加</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.02%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，例如借款</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">2</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天管理费为</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">0.22%</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">，以此类推。管理费在用户借款成功后在借到款项内扣除。</span></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"font-family: 宋体; color: red; font-size: 12pt\">借款成交费：</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">借款成交费是指借款成交后收取的费用，主要包含现场考察费，现场考察费由网站根据客户情况而定。线下签署协议。</span></p><p style=\"margin: 0cm 0cm 0pt\"><b><span style=\"font-family: 宋体; color: red; font-size: 12pt\">逾期催收费：</span></b></p><p style=\"margin: 0cm 0cm 0pt\"><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">借入者借款逾期时，如主动向网站声明并采取积极措施还款，网站将尽力帮助其解决困难。如果借款者恶意逃避，不接电话或者停机逃避债务，将受到罚息惩罚，逾期欠款每天加收逾期本金千分之四的罚息，逾期三十天以上加收电话催收费用每天千分之二，上门催收费用标准上限为：一线城市</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">1000</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">元</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">/</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">/</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">人，二线城市</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">800</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">元</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">/</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">/</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">人，三线城市为</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">500</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">元</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">/</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">天</span><span style=\"color: #333333; font-size: 12pt\"><font face=\"Times New Roman\">/</font></span><span style=\"font-family: 宋体; color: #333333; font-size: 12pt\">人，逾期欠款本息、罚息、电话催收费用必须直接转帐到本网站帐户，上门催收费用可由工作人员收取现金。对恶意逾期借入者，</span></p></div>','朱玮','1348388145','322','','','0','0','0','0','0');");
DB_I("replace  into `lzh_article` values('92','sdfdsfdsfsdf','','','<p><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt; font-weight: bold\">逾期催收方式与措施</span></span></p><p>&nbsp;</p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">用户借款后如确实遇到困难，必须主动联系网站说明原因，并做出还款计划，网站将为其进行债务重组，尽量帮助其缓解困难。</span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">用户如借款逾期以后，不接电话或恶意停机躲避债务等，网站将派人上门催收，除按逾期天数收取违约金和罚息，另外还将收取上门催收费用。</span><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">对于恶意骗款后逃跑的用户，网站将采取一切必要措施打击，收回欠款，并采取法律措施追究其责任。</span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\">&nbsp;</p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt; font-weight: bold\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 警告一切存心不良人士，请及时处理欠款，每一天的逾期，最终的后果由你本人承担。</span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt; font-weight: bold\">特别条款</span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">1</font><font face=\"宋体\">、借款人保证：借款人的借款用于合法用途，不得将所借款项用于任何违法活动</font><font face=\"微软雅黑\">(</font><font face=\"宋体\">包括但不限于赌博、吸毒、贩毒、卖淫嫖娼等</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">及一切高风险投资</font><font face=\"微软雅黑\">(</font><font face=\"宋体\">如证券期货、彩票等</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">。如借款人违反前述保证或有违反前述保证的嫌疑，则出借人有权采取下列措施：</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">(1)</font><font face=\"宋体\">宣布提前收回全部借款</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">(2)</font><font face=\"宋体\">出借人向公安等行政机关举报，追回此款并追究借款人的刑事责任，出借人和借款人均同意并授权本网站采取前述措施。</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">2</font><font face=\"宋体\">、本网站仅作为网友之间小额资金互助平台，借款人和出借人均不得利用本网站平台进行信用卡套现和其他</font><font face=\"微软雅黑\">(</font><font face=\"宋体\">如洗钱</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">不正当交易行为，否则出借人、借款人和本网站有权向公安等行政机关举报，追究其相关法律责任。</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">(1)</font><font face=\"宋体\">若借款者逾期仍未还款，除正常利息以外，对应还款额未还部分，还应支付千分之八</font><font face=\"微软雅黑\">/</font><font face=\"宋体\">天的逾期利息作为催收费用；逾期应还款由本网站通过短信、电话、上门等方式进行催收，暂不公开个人隐私资料。</font></span></p><p style=\"text-align: left; margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">　　<font face=\"微软雅黑\">(2)</font><font face=\"宋体\">借款者逾期还款，造成投资者因此支付的费用</font><font face=\"微软雅黑\">(</font><font face=\"宋体\">包括但不限于律师费</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">由借款者承担。本网站有权将借款人的有关资料正式备案在</font><font face=\"Times New Roman\">“</font><font face=\"宋体\">不良信用记录</font><font face=\"Times New Roman\">”</font><font face=\"宋体\">，列入全国个人信用评级体系的黑名单</font><font face=\"微软雅黑\">(</font><font face=\"Times New Roman\">“</font><font face=\"宋体\">不良信用记录</font><font face=\"Times New Roman\">”</font><font face=\"宋体\">数据将为银行、电信、担保公司、人才中心等有关机构提供个人不良信用信息</font><font face=\"微软雅黑\">)</font><font face=\"宋体\">，同时保留对借款者采取法律措施的权利，由此所产生的所有法律后果将由借款者来承担。</font></span></p><p style=\"margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"margin-top: 0pt; margin-bottom: 0pt\" class=\"p0\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\"><span style=\"font-family: 微软雅黑; color: rgb(51,51,51); font-size: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本页面内容仅供参考，以网站的公告与具体规定为准。</span></span></p>','admin','1351573591','321','','','0','0','0','0','0');");
